import{_ as t,I as i,c as a,o as l,J as r,b4 as n,b5 as s}from"./chunks/framework.D7_Q5RFF.js";const b=JSON.parse('{"title":"Watermark Configuration Options","description":"","frontmatter":{"layout":"doc"},"headers":[],"relativePath":"config/index.md","filePath":"config/index.md"}'),c={name:"config/index.md"};function d(g,e,h,u,p,f){const o=i("el-backtop");return l(),a("div",null,[r(o),e[0]||(e[0]=n('<h1 id="watermark-configuration-options" tabindex="-1">Watermark Configuration Options <a class="header-anchor" href="#watermark-configuration-options" aria-label="Permalink to “Watermark Configuration Options”">​</a></h1><h2 id="basic-configuration" tabindex="-1">Basic Configuration <a class="header-anchor" href="#basic-configuration" aria-label="Permalink to “Basic Configuration”">​</a></h2><h3 id="width" tabindex="-1">width <a class="header-anchor" href="#width" aria-label="Permalink to “width”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>300</code></li><li><strong>Description</strong>: Unit width for a single watermark element.</li></ul><h3 id="height" tabindex="-1">height <a class="header-anchor" href="#height" aria-label="Permalink to “height”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>300</code></li><li><strong>Description</strong>: Unit height for a single watermark element.</li></ul><h3 id="rotate" tabindex="-1">rotate <a class="header-anchor" href="#rotate" aria-label="Permalink to “rotate”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>45</code></li><li><strong>Description</strong>: Rotation angle of the watermark in degrees.</li></ul><h3 id="layout" tabindex="-1">layout <a class="header-anchor" href="#layout" aria-label="Permalink to “layout”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;default&#39;</code></li><li><strong>Description</strong>: Controls the arrangement of watermark elements</li><li><strong>Available Values</strong>: <ul><li><p><code>&#39;default&#39;</code> (Default mode)</p><ul><li>Basic layout with minimal configuration</li><li>Suitable for simple use cases</li></ul></li><li><p><code>&#39;grid&#39;</code> (Grid layout)</p><ul><li>Arranges watermarks in a matrix grid pattern</li><li>Requires <code>gridLayoutOptions</code> configuration</li><li>Typical use case: Generating patterned watermark backgrounds</li></ul></li></ul></li></ul><h3 id="gridlayoutoptions" tabindex="-1">gridLayoutOptions <a class="header-anchor" href="#gridlayoutoptions" aria-label="Permalink to “gridLayoutOptions”">​</a></h3><ul><li><strong>Type</strong>: <code>GridLayoutOptions</code></li><li><strong>Default</strong>: <code>null</code></li><li><strong>Description</strong>: Custom options for grid-based layout configuration.</li><li><strong>Properties</strong>: <ul><li><code>cols</code>: Number of columns (default: <code>1</code>)</li><li><code>rows</code>: Number of rows (default: <code>1</code>)</li><li><code>gap</code>: Spacing as <code>[horizontal, vertical]</code> (default: <code>[0, 0]</code>)</li><li><code>matrix</code>: 2D matrix controlling visibility per grid cell (default: all <code>1</code>s)</li><li><code>backgroundImage</code>: Optional background image (covers entire grid)</li><li><code>width</code>: Optional custom total width (overrides cols/gap calculation)</li><li><code>height</code>: Optional custom total height (overrides rows/gap calculation)</li></ul></li></ul><h3 id="auxiliaryline" tabindex="-1">auxiliaryLine <a class="header-anchor" href="#auxiliaryline" aria-label="Permalink to “auxiliaryLine”">​</a></h3><ul><li><strong>Type</strong>: <code>boolean</code></li><li><strong>Default</strong>: <code>false</code></li><li><strong>Description</strong>: Whether to display auxiliary guidelines for positioning.</li></ul><h2 id="positioning-movement" tabindex="-1">Positioning &amp; Movement <a class="header-anchor" href="#positioning-movement" aria-label="Permalink to “Positioning &amp; Movement”">​</a></h2><h3 id="translateplacement" tabindex="-1">translatePlacement <a class="header-anchor" href="#translateplacement" aria-label="Permalink to “translatePlacement”">​</a></h3><ul><li><strong>Type</strong>: <code>TranslatePlacementType</code></li><li><strong>Default</strong>: <code>&#39;middle&#39;</code></li><li><strong>Available Values</strong>: <ul><li><code>&#39;top&#39;</code>: Top center</li><li><code>&#39;top-start&#39;</code>: Top left</li><li><code>&#39;top-end&#39;</code>: Top right</li><li><code>&#39;bottom&#39;</code>: Bottom center</li><li><code>&#39;bottom-start&#39;</code>: Bottom left</li><li><code>&#39;bottom-end&#39;</code>: Bottom right</li><li><code>&#39;left&#39;</code>: Middle left</li><li><code>&#39;right&#39;</code>: Middle right</li><li><code>&#39;middle&#39;</code>: Center (default)</li></ul></li><li><strong>Description</strong>: <ul><li>Determines the base position for watermark placement and rotation transformation</li><li>Affects both the initial position and gradient calculations when using advanced styles</li><li>Automatically adjusts text alignment and baseline when not explicitly set</li><li>Coordinates with <code>translateX</code> and <code>translateY</code> for precise positioning</li></ul></li><li><strong>Note</strong>: <ul><li>When combined with rotation, the placement becomes the rotation center point</li><li>For text watermarks, this also affects text baseline calculation</li></ul></li></ul><h3 id="translatex" tabindex="-1">translateX <a class="header-anchor" href="#translatex" aria-label="Permalink to “translateX”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: Auto-calculated (based on <code>translatePlacement</code> and <code>width</code>)</li><li><strong>Description</strong>: <ul><li>Horizontal offset (in pixels)</li><li>Positive value → Right movement | Negative value ← Left movement</li><li>Overrides horizontal positioning from <code>translatePlacement</code></li><li>Serves as X-coordinate for rotation pivot point</li><li>Affects calculation of linear/radial gradient start points</li></ul></li></ul><h3 id="translatey" tabindex="-1">translateY <a class="header-anchor" href="#translatey" aria-label="Permalink to “translateY”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: Auto-calculated (based on <code>translatePlacement</code> and <code>height</code>)</li><li><strong>Description</strong>: <ul><li>Vertical offset (in pixels)</li><li>Positive value ↓ Downward movement | Negative value ↑ Upward movement</li><li>Overrides vertical positioning from <code>translatePlacement</code></li><li>Serves as Y-coordinate for rotation pivot point</li><li>Affects automatic calculation of text baseline (<code>textBaseline</code>)</li></ul></li></ul><h3 id="movable-animation" tabindex="-1">movable (animation) <a class="header-anchor" href="#movable-animation" aria-label="Permalink to “movable (animation)”">​</a></h3><ul><li><strong>Type</strong>: <code>boolean</code></li><li><strong>Default</strong>: <code>false</code></li><li><strong>Description</strong>:<br> When enabled, the watermark will exhibit physics-based animated motion. The behavior differs based on <code>backgroundRepeat</code>: <ul><li><code>repeat</code>: Full 2D floating animation (200s duration)</li><li><code>repeat-x</code>: Horizontal oscillation (random 2-8s duration)</li><li><code>repeat-y</code>: Vertical oscillation (random 2-4s duration)</li><li><code>no-repeat</code>: Combined horizontal+vertical movement. The watermark node will simulate collision-based movement within its parent element, automatically bouncing off boundaries.</li></ul></li><li><strong>Live Examples</strong>: <ul><li><a href="https://stackblitz.com/edit/webpack-webpack-js-org-wq26h43z" target="_blank" rel="noreferrer">StackBlitz</a></li><li><a href="https://zhensherlock.github.io/watermark-js-plus/guide/watermark.html#child-element-watermark" target="_blank" rel="noreferrer">Demo</a></li></ul></li><li><strong>Mechanism</strong>: <ul><li>Implemented via CSS <code>animation</code> with randomized durations</li><li>Uses <code>alternate</code> direction for ping-pong effect</li><li>Speed ranges: <ul><li>Horizontal: 2-8 seconds per cycle</li><li>Vertical: 2-4 seconds per cycle</li></ul></li></ul></li><li><strong>Note</strong>: <ul><li>Disables when <code>mutationObserve</code> is active</li><li>For physics-based movement, combine with <code>advancedStyle</code> gradients</li></ul></li></ul><h3 id="zindex" tabindex="-1">zIndex <a class="header-anchor" href="#zindex" aria-label="Permalink to “zIndex”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>2147483647</code></li><li><strong>Description</strong>: CSS z-index property for layering control.</li></ul><h3 id="parent" tabindex="-1">parent <a class="header-anchor" href="#parent" aria-label="Permalink to “parent”">​</a></h3><ul><li><strong>Type</strong>: <code>Element | string</code></li><li><strong>Default</strong>: <code>&#39;body&#39;</code></li><li><strong>Description</strong>: Container element for the watermark (selector or DOM element).</li></ul><h2 id="content-configuration" tabindex="-1">Content Configuration <a class="header-anchor" href="#content-configuration" aria-label="Permalink to “Content Configuration”">​</a></h2><h3 id="contenttype" tabindex="-1">contentType <a class="header-anchor" href="#contenttype" aria-label="Permalink to “contentType”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;text&#39;</code></li><li><strong>Available Values</strong>: <ul><li><code>&#39;text&#39;</code>: Simple text watermark</li><li><code>&#39;image&#39;</code>: Image watermark</li><li><code>&#39;multi-line-text&#39;</code>: Multi-line text watermark</li><li><code>&#39;rich-text&#39;</code>: Formatted rich text watermark</li></ul></li><li><strong>Description</strong>: Type of content to display as watermark.</li></ul><h3 id="content" tabindex="-1">content <a class="header-anchor" href="#content" aria-label="Permalink to “content”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;hello watermark-js-plus&#39;</code></li><li><strong>Description</strong>: The actual content to display (text or image URL).</li></ul><h3 id="texttype" tabindex="-1">textType <a class="header-anchor" href="#texttype" aria-label="Permalink to “textType”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;fill&#39;</code></li><li><strong>Available Values</strong>: <code>&#39;fill&#39;</code> | <code>&#39;stroke&#39;</code></li><li><strong>Description</strong>: Rendering method for text content.</li></ul><h2 id="text-styling" tabindex="-1">Text Styling <a class="header-anchor" href="#text-styling" aria-label="Permalink to “Text Styling”">​</a></h2><h3 id="fontsize" tabindex="-1">fontSize <a class="header-anchor" href="#fontsize" aria-label="Permalink to “fontSize”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;20px&#39;</code></li><li><strong>Description</strong>: Font size for text content.</li></ul><h3 id="fontfamily" tabindex="-1">fontFamily <a class="header-anchor" href="#fontfamily" aria-label="Permalink to “fontFamily”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;sans-serif&#39;</code></li><li><strong>Description</strong>: Font family for text content.</li></ul><h3 id="fontstyle" tabindex="-1">fontStyle <a class="header-anchor" href="#fontstyle" aria-label="Permalink to “fontStyle”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;&#39;</code></li><li><strong>Description</strong>: Font style (e.g., &#39;italic&#39;).</li></ul><h3 id="fontvariant" tabindex="-1">fontVariant <a class="header-anchor" href="#fontvariant" aria-label="Permalink to “fontVariant”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;&#39;</code></li><li><strong>Description</strong>: Font variant (e.g., &#39;small-caps&#39;).</li></ul><h3 id="fontweight" tabindex="-1">fontWeight <a class="header-anchor" href="#fontweight" aria-label="Permalink to “fontWeight”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;normal&#39;</code></li><li><strong>Description</strong>: Font weight (e.g., &#39;bold&#39;).</li></ul><h3 id="fontcolor" tabindex="-1">fontColor <a class="header-anchor" href="#fontcolor" aria-label="Permalink to “fontColor”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;#000&#39;</code></li><li><strong>Description</strong>: Text color.</li></ul><h3 id="textalign" tabindex="-1">textAlign <a class="header-anchor" href="#textalign" aria-label="Permalink to “textAlign”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Available Values</strong>: <code>&#39;center&#39;</code>, <code>&#39;end&#39;</code>, <code>&#39;left&#39;</code>, <code>&#39;right&#39;</code>, <code>&#39;start&#39;</code></li><li><strong>Description</strong>: Horizontal text alignment.</li></ul><h3 id="textbaseline" tabindex="-1">textBaseline <a class="header-anchor" href="#textbaseline" aria-label="Permalink to “textBaseline”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Available Values</strong>: <ul><li><code>&#39;top&#39;</code>, <code>&#39;bottom&#39;</code>, <code>&#39;middle&#39;</code></li><li><code>&#39;alphabetic&#39;</code>, <code>&#39;hanging&#39;</code>, <code>&#39;ideographic&#39;</code></li></ul></li><li><strong>Description</strong>: Vertical text alignment baseline. <img src="'+s+'" alt="textBaseline"></li></ul><h3 id="lineheight" tabindex="-1">lineHeight <a class="header-anchor" href="#lineheight" aria-label="Permalink to “lineHeight”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>30</code></li><li><strong>Description</strong>: Line height for multi-line text.</li></ul><h3 id="textrowmaxwidth" tabindex="-1">textRowMaxWidth <a class="header-anchor" href="#textrowmaxwidth" aria-label="Permalink to “textRowMaxWidth”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Description</strong>: Maximum width for text rows before wrapping.</li></ul><h3 id="letterspacing" tabindex="-1">letterSpacing <a class="header-anchor" href="#letterspacing" aria-label="Permalink to “letterSpacing”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;0px&#39;</code></li><li><strong>Description</strong>: Spacing between characters.</li></ul><h3 id="wordspacing" tabindex="-1">wordSpacing <a class="header-anchor" href="#wordspacing" aria-label="Permalink to “wordSpacing”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;0px&#39;</code></li><li><strong>Description</strong>: Spacing between words.</li></ul><h2 id="image-configuration" tabindex="-1">Image Configuration <a class="header-anchor" href="#image-configuration" aria-label="Permalink to “Image Configuration”">​</a></h2><h3 id="image" tabindex="-1">image <a class="header-anchor" href="#image" aria-label="Permalink to “image”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Description</strong>: URL of the image to use as watermark (when contentType = &#39;image&#39;).</li></ul><h3 id="imagewidth" tabindex="-1">imageWidth <a class="header-anchor" href="#imagewidth" aria-label="Permalink to “imageWidth”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>0</code></li><li><strong>Description</strong>: Display width for the image (0 = natural width).</li></ul><h3 id="imageheight" tabindex="-1">imageHeight <a class="header-anchor" href="#imageheight" aria-label="Permalink to “imageHeight”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>0</code></li><li><strong>Description</strong>: Display height for the image (0 = natural height).</li></ul><h2 id="rich-text-configuration" tabindex="-1">Rich Text Configuration <a class="header-anchor" href="#rich-text-configuration" aria-label="Permalink to “Rich Text Configuration”">​</a></h2><h3 id="richtextwidth" tabindex="-1">richTextWidth <a class="header-anchor" href="#richtextwidth" aria-label="Permalink to “richTextWidth”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Description</strong>: Width constraint for rich text content.</li></ul><h3 id="richtextheight" tabindex="-1">richTextHeight <a class="header-anchor" href="#richtextheight" aria-label="Permalink to “richTextHeight”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Description</strong>: Height constraint for rich text content.</li></ul><h2 id="visual-effects" tabindex="-1">Visual Effects <a class="header-anchor" href="#visual-effects" aria-label="Permalink to “Visual Effects”">​</a></h2><h3 id="globalalpha" tabindex="-1">globalAlpha <a class="header-anchor" href="#globalalpha" aria-label="Permalink to “globalAlpha”">​</a></h3><ul><li><strong>Type</strong>: <code>number</code></li><li><strong>Default</strong>: <code>0.5</code></li><li><strong>Description</strong>: Overall transparency of the watermark (0.0 to 1.0).</li></ul><h3 id="filter" tabindex="-1">filter <a class="header-anchor" href="#filter" aria-label="Permalink to “filter”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;none&#39;</code></li><li><strong>Description</strong>: Applies CSS filter effects similar to those available for CSS. The value is a string containing one or more of the following filter functions. <ul><li><code>url()</code>: Applies an SVG filter defined in an external resource (e.g., <code>url(#custom-filter)</code>)</li><li><code>blur(&lt;length&gt;)</code>: Applies a Gaussian blur (e.g., <code>blur(5px)</code>)</li><li><code>brightness(&lt;percentage&gt;)</code>: Adjusts brightness (e.g., <code>brightness(150%)</code>)</li><li><code>contrast(&lt;percentage&gt;)</code>: Adjusts contrast (e.g., <code>contrast(75%)</code>)</li><li><code>drop-shadow(&lt;offset-x&gt; &lt;offset-y&gt; &lt;blur-radius&gt; &lt;color&gt;)</code>: Applies a drop shadow (e.g., <code>drop-shadow(4px 4px 8px blue)</code>)</li><li><code>grayscale(&lt;percentage&gt;)</code>: Converts to grayscale (e.g., <code>grayscale(100%)</code>)</li><li><code>hue-rotate(&lt;angle&gt;)</code>: Applies hue rotation (e.g., <code>hue-rotate(90deg)</code>)</li><li><code>invert(&lt;percentage&gt;)</code>: Inverts colors (e.g., <code>invert(50%)</code>)</li><li><code>opacity(&lt;percentage&gt;)</code>: Adjusts opacity (e.g., <code>opacity(25%)</code>)</li><li><code>saturate(&lt;percentage&gt;)</code>: Adjusts saturation (e.g., <code>saturate(200%)</code>)</li><li><code>sepia(&lt;percentage&gt;)</code>: Applies sepia tone (e.g., <code>sepia(80%)</code>)</li></ul></li></ul><p>Multiple filters can be combined by space-separating them (e.g., <code>brightness(120%) contrast(110%)</code>). The value <code>&#39;none&#39;</code> indicates no filter effect.</p><h3 id="shadowstyle" tabindex="-1">shadowStyle <a class="header-anchor" href="#shadowstyle" aria-label="Permalink to “shadowStyle”">​</a></h3><ul><li><strong>Type</strong>: <code>CanvasShadowStyles</code></li><li><strong>Default</strong>: <code>null</code></li><li><strong>Properties</strong>: <ul><li><code>shadowColor</code>: <code>string</code><br> Shadow color (supports all CSS color formats)</li><li><code>shadowBlur</code>: <code>number</code><br> Blur level (in pixels, 0 = no blur)</li><li><code>shadowOffsetX</code>: <code>number</code><br> Horizontal offset (in pixels, positive = right)</li><li><code>shadowOffsetY</code>: <code>number</code><br> Vertical offset (in pixels, positive = down)</li></ul></li><li><strong>Description</strong>: Adds standard Canvas shadow effects with features: <ul><li>Supports transparency (alpha channel)</li><li>Offsets are unaffected by <code>rotate</code>/<code>translate</code></li><li>Combines multiplicatively with <code>globalAlpha</code></li></ul></li><li><strong>Note</strong>: <ul><li>Requires <code>shadowColor</code> to be set to take effect</li><li>Blur calculations may impact rendering performance</li><li>May appear subtle at low opacity (<code>globalAlpha &lt; 0.3</code>)</li></ul></li></ul><h3 id="advancedstyle" tabindex="-1">advancedStyle <a class="header-anchor" href="#advancedstyle" aria-label="Permalink to “advancedStyle”">​</a></h3><ul><li><strong>Type</strong>: <code>AdvancedStyleType</code></li><li><strong>Default</strong>: <code>null</code></li><li><strong>Properties</strong>: <ul><li><code>type</code>: <code>&#39;linear&#39; | &#39;radial&#39; | &#39;conic&#39; | &#39;pattern&#39;</code><br> Gradient type (linear/radial/conic/pattern)</li><li><code>params</code>: Configuration object <ul><li><code>linear</code>: { x0, y0, x1, y1 }<br> Start/end coordinates for linear gradient</li><li><code>radial</code>: { x0, y0, r0, x1, y1, r1 }<br> Circle parameters for radial gradient</li><li><code>conic</code>: { startAngle, x, y }<br> Starting angle and center point for conic gradient</li><li><code>pattern</code>: { image, repetition }<br> Pattern fill settings</li></ul></li><li><code>colorStops</code>: <code>Array&lt;{ offset: number, color: string }&gt;</code><br> Color stop positions and values</li></ul></li><li><strong>Description</strong>: Enables advanced fill styles that override default <code>fontColor</code>. Supports: <ul><li>All CSS gradient types (auto-adapted to Canvas coordinates)</li><li>Image pattern fills (with cross-origin support)</li><li>Smart coordinate positioning (auto-adjusts gradient points based on <code>translatePlacement</code>)</li></ul></li><li><strong>Note</strong>: <ul><li>Works with <code>textType</code> (applies to both <code>fillStyle</code> and <code>strokeStyle</code>)</li><li>Requires preloaded images for <code>pattern</code> type</li><li>Coordinate system affected by <code>rotate</code> and <code>translateX/Y</code> transforms</li></ul></li></ul><h3 id="backgroundposition" tabindex="-1">backgroundPosition <a class="header-anchor" href="#backgroundposition" aria-label="Permalink to “backgroundPosition”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;0 0&#39;</code></li><li><strong>Description</strong>: CSS background-position property.</li></ul><h3 id="backgroundrepeat" tabindex="-1">backgroundRepeat <a class="header-anchor" href="#backgroundrepeat" aria-label="Permalink to “backgroundRepeat”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;repeat&#39;</code></li><li><strong>Description</strong>: CSS background-repeat property.</li></ul><h2 id="behavior-security" tabindex="-1">Behavior &amp; Security <a class="header-anchor" href="#behavior-security" aria-label="Permalink to “Behavior &amp; Security”">​</a></h2><h3 id="mode" tabindex="-1">mode <a class="header-anchor" href="#mode" aria-label="Permalink to “mode”">​</a></h3><ul><li><strong>Type</strong>: <code>string</code></li><li><strong>Default</strong>: <code>&#39;default&#39;</code></li><li><strong>Available Values</strong>: <ul><li><code>&#39;default&#39;</code>: Standard visible watermark mode (normal transparency)</li><li><code>&#39;blind&#39;</code>: Invisible watermark mode (extremely low transparency) for covert protection</li></ul></li><li><strong>Description</strong>: Determines the watermark visibility behavior. <ul><li>In <code>default</code> mode, the watermark is visibly displayed with configured transparency.</li><li>In <code>blind</code> mode, the watermark becomes nearly invisible (fixed at globalAlpha=0.005) while remaining detectable through decoding.</li></ul></li></ul><h3 id="mutationobserve" tabindex="-1">mutationObserve <a class="header-anchor" href="#mutationobserve" aria-label="Permalink to “mutationObserve”">​</a></h3><ul><li><strong>Type</strong>: <code>boolean</code></li><li><strong>Default</strong>: <code>true</code></li><li><strong>Description</strong>: Whether to monitor DOM for unauthorized changes.</li></ul><h3 id="monitorprotection" tabindex="-1">monitorProtection <a class="header-anchor" href="#monitorprotection" aria-label="Permalink to “monitorProtection”">​</a></h3><ul><li><strong>Type</strong>: <code>boolean</code></li><li><strong>Default</strong>: <code>false</code></li><li><strong>Description</strong>: Enable protection against MutationObserver tampering.</li><li><strong>Important</strong>: Once enabled, this protection cannot be disabled.</li></ul><h2 id="callbacks" tabindex="-1">Callbacks <a class="header-anchor" href="#callbacks" aria-label="Permalink to “Callbacks”">​</a></h2><h3 id="extradrawfunc" tabindex="-1">extraDrawFunc <a class="header-anchor" href="#extradrawfunc" aria-label="Permalink to “extraDrawFunc”">​</a></h3><ul><li><strong>Type</strong>: <code>Function</code></li><li><strong>Default</strong>: <code>() =&gt; {}</code></li><li><strong>Description</strong>: Additional drawing operations callback.</li></ul><h3 id="onsuccess" tabindex="-1">onSuccess <a class="header-anchor" href="#onsuccess" aria-label="Permalink to “onSuccess”">​</a></h3><ul><li><strong>Type</strong>: <code>Function</code></li><li><strong>Default</strong>: <code>() =&gt; {}</code></li><li><strong>Description</strong>: Called when watermark is successfully applied.</li></ul><h3 id="onbeforedestroy" tabindex="-1">onBeforeDestroy <a class="header-anchor" href="#onbeforedestroy" aria-label="Permalink to “onBeforeDestroy”">​</a></h3><ul><li><strong>Type</strong>: <code>Function</code></li><li><strong>Default</strong>: <code>() =&gt; {}</code></li><li><strong>Description</strong>: Called before watermark removal.</li></ul><h3 id="ondestroyed" tabindex="-1">onDestroyed <a class="header-anchor" href="#ondestroyed" aria-label="Permalink to “onDestroyed”">​</a></h3><ul><li><strong>Type</strong>: <code>Function</code></li><li><strong>Default</strong>: <code>() =&gt; {}</code></li><li><strong>Description</strong>: Called after watermark removal.</li></ul><h3 id="onobserveerror" tabindex="-1">onObserveError <a class="header-anchor" href="#onobserveerror" aria-label="Permalink to “onObserveError”">​</a></h3><ul><li><strong>Type</strong>: <code>Function</code></li><li><strong>Default</strong>: <code>() =&gt; {}</code></li><li><strong>Description</strong>: Called when mutation observation fails.</li></ul>',103))])}const y=t(c,[["render",d]]);export{b as __pageData,y as default};
